import React from 'react'
import BeforeQuiz from '../components/beforeQuiz'
import DuringQuiz from '../components/duringQuiz'
import AfterQuiz from '../components/afterQuiz'
import { questionsFromBank } from '../questionsBank'
 
class Quiz extends React.Component  {
    state = {
        beforeQuizHidden: false,
        duringQuizHidden: true,
        afterQuizHidden: true,
        questions: {},
        score: 0,
        timeInSeconds: 0,
        timeRanOut: false,
        difficultyOptions: [
            {
                difficulty: 'Easy',
                numberOfQuestions: 12,
                timeInMin: 2
            },
            {
                difficulty: 'Medium',
                numberOfQuestions: 15,
                timeInMin: 1.5
            },
            {
                difficulty: 'Hard',
                numberOfQuestions: 20,
                timeInMin: 1
            }
        ]
    }

    startQuiz = () => {
        this.setState({
            beforeQuizHidden: true,
            duringQuizHidden: false
        })
    }

    endQuiz = () => {
        this.setState({
            duringQuizHidden: true,
            afterQuizHidden: false,
        })
    }

    restartQuiz = () => {
        this.setState({
            beforeQuizHidden: false,
            afterQuizHidden: true,
            timeRanOut: false
        })
    }

    generateQuestions = (numberOfQuestions) => {
        this.setState({
            questions: questionsFromBank(numberOfQuestions)
        })
    }

    getScore = (finalScore) => {
        this.setState({
            score: finalScore
        })
    }

    setTimer = (updatedTime) => {
        this.setState({
            timeInSeconds: updatedTime
        })
    }

    updateTimeRanOut = () => {
        this.setState({
            timeRanOut: true
        })
    }
 
    render() {
        const { startQuiz, endQuiz, restartQuiz, generateQuestions, getScore, setTimer, updateTimeRanOut } = this.props
        const { difficultyOptions, beforeQuizHidden, duringQuizHidden, afterQuizHidden, questions, score, timeInSeconds, timeRanOut } = this.state
 
        console.log(difficultyOptions)
        return (
            <div>
                {!beforeQuizHidden && <BeforeQuiz
                startQuiz = {startQuiz}
                difficultyOptions = {difficultyOptions}
                generateQuestions = {generateQuestions}
                timeInSeconds = {timeInSeconds}
                setTimer = {setTimer}
                />}
 
                {!duringQuizHidden && <DuringQuiz
                questions = {questions}
                endQuiz = {endQuiz}
                getScore = {getScore}
                timeInSeconds = {timeInSeconds}
                timeRanOut = {timeRanOut}
                updateTimeRanOut = {updateTimeRanOut}
                />}
 
                {!afterQuizHidden && <AfterQuiz
                questions = {questions}
                restartQuiz = {restartQuiz}
                score = {score}
                timeInSeconds = {timeInSeconds}
                timeRanOut = {timeRanOut}
                />}
            </div>
        )
    }
}
 
export default Quiz
